import React, { useState, Component } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  Button,
  ScrollView,
  FlatList,
} from "react-native";
import GoalItem from "./components/GoalItem";
import GoalInput from "./components/GoalInput";
import { storeTodoNote } from "./firebase";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = { courseGoals: [] };
  }
  componentDidMount() {
    // consÃ·t [this.state.courseGoals, setCourseGoals] = useState([]);
  }
  setCourseGoals(goals) {
    this.state.courseGoals = goals;
  }
  addGoalHandler(goalTitle) {
    setCourseGoals((currentGoals) => [
      ...currentGoals,
      { key: Math.random().toString(), value: goalTitle },
    ]);
    storeTodoNote({ key: Math.random().toString(), value: goalTitle });
  }
  render() {
    return (
      <View style={styles.screen}>
        <GoalInput addGoal={addGoalHandler} />
        <FlatList
          keyExtractor={(item, index) => item.key}
          data={this.state.courseGoals}
          renderItem={(itemData) => (
            <GoalItem
              id={itemData.item.key}
              onDelete={removeGoalHandler}
              title={itemData.item.value}
            />
          )}
        />
      </View>
    );
  }
}

const removeGoalHandler = (goalId) => {
  setCourseGoals((currentGoals) => {
    return currentGoals.filter((goal) => goal.key !== goalId);
  });
};
const styles = StyleSheet.create({
  screen: { padding: 50 },
});
